export default function cart(state = {
    cart: []
  }, action) {
    switch (action.type) {

      case 'ADD_GAME':

        return {
          ...state,
          cart: action.payload
        }

      case 'INCREASE_QUANTITY':

        const increaseIndex = state.cart.findIndex( game => {
          return game.id === action.payload
        })

        debugger
        return [
          ...state.cart.slice(0, increaseIndex),
          Object.assign({}, state[increaseIndex], {quantity: state[increaseIndex].quantity += 1}),
          ...state.cart.slice(increaseIndex + 1)
        ]
        //find index
        //return state with new index
        return {
          ...state, cart: action.payload
        }

      default:
        return state;

    }
  };
